import { useContext } from "react";
import { AuthContext } from "../../context/authContext";
import "./comments.scss";

const Comments = () => {
  const { currentUser } = useContext(AuthContext);
  //temp data
  const comments = [
    {
      id: 1,
      desc: "aklsjglkdjaslkgaksgjlkasdjlgkjas;ljga;lskjg;lkjd;lkgjaslkjdgl;asjdgl;kjsal;gkj;lhgrwqoigh;owlihgj;lskag;lkdsja;lkjsl;gjoirwghlskaglksdjgl;ksjal;j lasd;glk jlkjaslks glksja lkjlkjad",
      name: "John Doe",
      userId: 1,
      profilePicture:
        "https://images.unsplash.com/photo-1566492031773-4f4e44671857?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MjB8fGF2YXRhcnxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=500&q=60",
    },
    {
      id: 2,
      desc: "aklsjglkdjaslkgj lasd;glk jlkjaslks glksja lkjlkjad",
      name: "John Doe",
      userId: 1,
      profilePicture:
        "https://images.unsplash.com/photo-1566492031773-4f4e44671857?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MjB8fGF2YXRhcnxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=500&q=60",
    },
    {
      id: 3,
      desc: "aklsjglkdjaslkgj lasd;glk jlkjaslks glksja lkjlkjad",
      name: "John Doe",
      userId: 1,
      profilePicture:
        "https://images.unsplash.com/photo-1566492031773-4f4e44671857?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MjB8fGF2YXRhcnxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=500&q=60",
    },
    {
      id: 4,
      desc: "aklsjglkdjaslkgj lasd;glk jlkjaslks glksja lkjlkjad",
      name: "John Doe",
      userId: 1,
      profilePicture:
        "https://images.unsplash.com/photo-1566492031773-4f4e44671857?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MjB8fGF2YXRhcnxlbnwwfHwwfHw%3D&auto=format&fit=crop&w=500&q=60",
    },
  ];
  return (
    <div className="comments">
      <div className="write">
        <img src={currentUser.profilePic} alt="" />
        <input type="text" placeholder="Write a comment" />
        <button>Send</button>
      </div>
      {comments.map((comment) => (
        <div className="comment">
          <img src={comment.profilePicture} alt="" />
          <div className="info">
            <span>{comment.name}</span>
            <p>{comment.desc}</p>
          </div>
          <span className="date">1 hour ago</span>
        </div>
      ))}
    </div>
  );
};

export default Comments;
