import { Link } from "react-router-dom";
import "./register.scss";
import axios from "axios";
import { useState } from "react";

const Register = () => {
  const [inputs, setInputs] = useState({
    username: "",
    email: "",
    password: "",
    name: "",
  });
  const [err, setErr] = useState(false);

  const handleChange = (e) => {
    setInputs((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleClick = async (e) => {
    e.preventDefault();

    try {
      await axios.post("http://localhost:8000/api/auth/register", inputs);
    } catch (err) {
      setErr(true);
    }
  };
  return (
    <div className="register">
      <div className="card">
        <div className="left">
          <h1>K1 Social</h1>
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Esse non a
            voluptas reprehenderit? Expedita, voluptates minima quos facilis est
            tempore atque! Natus, atque fuga! Dolor, at hic! Quam, debitis
            blanditiis.
          </p>
          <span>Do you have an account?</span>
          <Link to="/login">
            <button>Login</button>
          </Link>
        </div>
        <div className="right">
          <h1>Register</h1>
          <form>
            <input
              type="text"
              placeholder="Username"
              name="username"
              onChange={handleChange}
            />
            <input
              type="email"
              placeholder="Email"
              name="email"
              onChange={handleChange}
            />
            <input
              type="password"
              placeholder="Password"
              name="password"
              onChange={handleChange}
            />
            <input
              type="text"
              placeholder="Name"
              name="name"
              onChange={handleChange}
            />
            {err && e}
            <button onClick={handleClick}>Register</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
